export default function HeroSvg() {
  const { theme } = useTheme();
  const [mounted, setMounted] = useState(false);

  useEffect(() => setMounted(true), []);

  if (!mounted) return null;

  return theme === "dark" ? onDarkTheme() : onLightTheme();
}